package calculatorimport calculator.dto.ExpressionRequestimport calculator.dto.ExpressionResponseimport calculator.util.operate.ver1.Operatorclass Calculator {    fun calculate(expression: ExpressionRequest): ExpressionResponse {        val request = expression.request        val calculateList = request.split(" ")        val num1 = calculateList[0].toInt()        val sign = calculateList[1]        val num2 = calculateList[2].toInt()        return when (sign) {            "+" -> ExpressionResponse(Operator.PLUS.operate(num1, num2))            "-" -> ExpressionResponse(Operator.MINUS.operate(num1, num2))            "*" -> ExpressionResponse(Operator.MULTIPLY.operate(num1, num2))            "/" -> ExpressionResponse(Operator.DIVIDE.operate(num1, num2))            else -> throw RuntimeException("계산 불가능함")        }    }}