package calculatorimport calculator.controller.CalcControllerimport calculator.io.Consoleimport kotlin.system.exitProcessfun main() {    val controller: CalcController = CalcController(Calculator())    val console = Console()    while (true) {        console.printMenu()        when (val selectMenu = console.read()) {            "1" -> {                val request = console.getRequest()                val result = controller.getCommand(request)                console.printResult(result.answer)            }            "2" -> {                console.print("종료합니다.")                exitProcess(0)            }        }    }}