package calculator.ioimport calculator.dto.ExpressionRequestclass Console {    private val menu = "1. 계산 / 2. 종료"    // INPUT    fun read() = readln()    fun getRequest(): ExpressionRequest {        val request = readln()        return ExpressionRequest(request)    }    // OUTPUT    fun print(message: Any?) = println(message)    fun printMenu() = println("$menu")    fun printResult(message: Any?) {        println("결과 값 :  $message")    }}