package calculator.util.operate.ver0import java.lang.RuntimeExceptionabstract class Operator {    abstract fun operate(o1: Int, o2: Int): Int}class AddOperator : Operator() {    override fun operate(o1: Int, o2: Int) = o1 + o2}class MinusOperator : Operator() {    override fun operate(o1: Int, o2: Int) = o1 - o2}class MultiplyOperator : Operator() {    override fun operate(o1: Int, o2: Int) = o1 * o2}class DivideOperator : Operator() {    override fun operate(o1: Int, o2: Int) = if (o2 != 0) o1 / o2 else throw RuntimeException("0으로 나눌 수 없습니다.")}