package calculator.util.operate.ver1import java.lang.RuntimeExceptionenum class Operator {    PLUS {        override fun operate(o1: Int, o2: Int) = o1 + o2    },    MINUS {        override fun operate(o1: Int, o2: Int) = o1 - o2    },    MULTIPLY {        override fun operate(o1: Int, o2: Int) = o1 * o2    },    DIVIDE {        override fun operate(o1: Int, o2: Int) = if (o2 != 0) o1 / o2 else throw RuntimeException("0으로 나눌 수 없습니다.")    };    abstract fun operate(o1: Int, o2: Int): Int}